mapper.update:
  		UPDATE BOARD 
	  			SET CATEGORY_NO = #{categoryNo},
		  			BOARD_TITLE = #{boardTitle},
		  			BOARD_CONTENT = #{boardContent},
		  			PRICE = #{price}
  			WHERE BOARD_NO = #{boardNo}

UPDATE BOARD
	SET STATUS = 'N'
WHERE BOARD_NO = #{boardNo}

UPDATE NOTICE
	SET STATUS = 'N'
WHERE NOTICE_NO = #{noticeNo}

mapper.select:
  		SELECT  BOARD_NO,
  				CATEGORY_NO,
  				BOARD_TITLE,
  				BOARD_CONTENT,
  				PRICE,
  				BOARD_TYPE,
  				MEM_NO
  		FROM BOARD
  		WHERE CATEGORY_NO = #{categoryNo}
  		AND		BOARD_TITLE = #{boardTitle}
  		AND		BOARD_CONTENT = #{boardContent}
		AND		PRICE = #{price}
		AND		BOARD_TYPE = #{boardType}
		AND		MEM_NO = #{memberNo}

  		SELECT  BOARD_NO,
  				CATEGORY_NO,
  				BOARD_TITLE,
  				BOARD_CONTENT,
  				PRICE,
  				BOARD_TYPE,
  				MEM_NO
  		FROM BOARD
  		WHERE CATEGORY_NO = #{categoryNo}
  		AND		BOARD_TITLE = #{boardTitle}
  		AND		BOARD_CONTENT = #{boardContent}
		AND		PRICE = #{price}
		AND		BOARD_TYPE = #{boardType}
		AND		MEM_NO = #{memberNo}

mapper.update:
  		UPDATE BOARD
	  		SET CATEGORY_NO = #{categoryNo},
	  			BOARD_TITLE = #{boardTitle},
	  			BOARD_CONTENT = #{boardContent},
	  			PRICE = #{price}
