UPDATE BOARD
	SET BOARD_TITLE = #{boardTitle},
		BOARD_CONTENT = #{boardContent}
WHERE BOARD_NO = #{boardNo}
	AND STATUS = 'Y'

UPDATE BOARD
	SET STATUS = 'N'
WHERE BOARD_NO = #{boardNo}

UPDATE REPLY

	SET STATUS = 'N'
WHERE REPLY_NO = #{replyNo}

mapper.select:
SELECT LIKEY_COUNT
	FROM BOARD
WHERE BOARD_NO =#{boardNo}

mapper.update:
UPDATE LIKEY
	SET LISTATUS = 'Y'
WHERE BOARD_NO = #{boardNo}
 AND MEM_NO = #{memberNo}

UPDATE LIKEY
	SET LISTATUS = 'N'
WHERE BOARD_NO = #{boardNo}
 AND MEM_NO = #{memberNo}
 AND ROWNUM <= 1

mapper.insert:
		INSERT INTO LIKEY (LIKEY_NO, BOARD_NO, MEM_NO)
    	SELECT SEQ_LNO.NEXTVAL, #{boardNo}, #{memberNo}
   		FROM DUAL
    	WHERE NOT EXISTS (
      	SELECT 1 FROM LIKEY WHERE BOARD_NO = #{boardNo} 
      							AND MEM_NO = #{memberNo}					
    )

mapper.update:

UPDATE BOARD
	SET VIEW_COUNT = VIEW_COUNT + 1
WHERE BOARD_NO = #{boardNo}
